import React, { Component } from 'react';
import axios from 'axios';
import { SERVER_HOST, ACCESS_LEVEL_ADMIN } from '../config/global_constants';


class PurchaseHistory extends Component {
    constructor(props) {
        super(props);
        this.state = {
            purchases: []
        };
    }

    componentDidMount() {
        this.fetchPurchaseHistory();
    }

    fetchPurchaseHistory = () => {
        axios.get(`${SERVER_HOST}/sales`, {
            headers: { "authorization": localStorage.token }
        })
        .then(response => {
            this.setState({ purchases: response.data });
        })
        .catch(error => {
            console.error("Error fetching purchase history", error);
        });
    }

    render() {
        const { purchases } = this.state;

        return (
            <div>
                <h2>Purchase History</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Items</th>
                            <th>Price</th>
                            <th>Customer</th>
                            <th>Payment ID</th>
                        </tr>
                    </thead>
                    <tbody>
                        {purchases.map((purchase, index) => (
                            <tr key={index}>
                                <td>{new Date(purchase.purchaseDate).toLocaleDateString()}</td>
                                <td>
                                    {purchase.items.map((item, itemIndex) => (
                                        <div key={itemIndex}>{item.name} (Size: {item.size})</div>
                                    ))}
                                </td>
                                <td>${purchase.price.toFixed(2)}</td>
                                <td>{purchase.customerName}</td>
                                <td>{purchase.paypalPaymentID}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default PurchaseHistory;
