import React, { Component } from 'react';
import SearchJersey from './SearchJersey';
import FilterJersey from './FilterJersey';

class JerseyTableRow extends React.Component {
    render() {
        const { jersey } = this.props;
        return (
            <tr>
                <td>{jersey.team}</td>
                <td>{jersey.player}</td>
                <td>{jersey.size}</td>
                <td>${jersey.price}</td>
            </tr>
        );
    }
}

class JerseyTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchTerm: '',
            teamFilter: '',
            sizeFilter: '',
            jerseys: props.jerseys, // Assuming this prop is passed in with jersey data
        };
    }

    handleSearchTermChange = (newSearchTerm) => {
        this.setState({ searchTerm: newSearchTerm });
    };

    handleFilterChange = (filterName, value) => {
        this.setState({ [filterName]: value });
    };

    getFilteredJerseys() {
        const { jerseys, searchTerm, teamFilter, sizeFilter } = this.state;
        return jerseys.filter((jersey) => {
            const searchTermMatch = jersey.player.toLowerCase().includes(searchTerm.toLowerCase()) || jersey.team.toLowerCase().includes(searchTerm.toLowerCase());
            const teamMatch = teamFilter ? jersey.team === teamFilter : true;
            const sizeMatch = sizeFilter ? jersey.size === sizeFilter : true;
            return searchTermMatch && teamMatch && sizeMatch;
        });
    }

    render() {
        const filteredJerseys = this.getFilteredJerseys();
        return (
            <div>
                <SearchJersey
                    searchTerm={this.state.searchTerm}
                    onSearchTermChange={this.handleSearchTermChange}
                />
                <FilterJersey
                    teamFilter={this.state.teamFilter}
                    sizeFilter={this.state.sizeFilter}
                    onFilterChange={this.handleFilterChange}
                />
                <table>
                    <thead>
                        <tr>
                            <th>Team</th>
                            <th>Player</th>
                            <th>Size</th>
                            <th>Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredJerseys.map((jersey, index) => (
                            <JerseyTableRow key={index} jersey={jersey} />
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default JerseyTable;
