// JerseyTable.js
import React, { Component } from 'react';
import SearchJersey from './SearchJersey';
import FilterJersey from './FilterJersey';
import JerseyTableRow from './JerseyTableRow';

class JerseyTable extends Component {
    state = {
        searchTerm: '',
        teamFilter: '',
        sizeFilter: '',
        jerseys: this.props.jerseys, // Assuming jerseys prop contains the data
    };

    handleSearchTermChange = (searchTerm) => {
        this.setState({ searchTerm });
    };

    handleFilterChange = (filterName, value) => {
        this.setState({ [filterName]: value });
    };

    getFilteredJerseys = () => {
        return this.state.jerseys.filter(jersey => {
            return (
                (this.state.searchTerm ? jersey.player.toLowerCase().includes(this.state.searchTerm.toLowerCase()) : true) &&
                (this.state.teamFilter ? jersey.team === this.state.teamFilter : true) &&
                (this.state.sizeFilter ? jersey.size === this.state.sizeFilter : true)
            );
        });
    };

    render() {
        const filteredJerseys = this.getFilteredJerseys();

        return (
            <div>
                <SearchJersey
                    searchTerm={this.state.searchTerm}
                    onSearchTermChange={this.handleSearchTermChange}
                />
                <FilterJersey
                    teamFilter={this.state.teamFilter}
                    sizeFilter={this.state.sizeFilter}
                    onFilterChange={this.handleFilterChange}
                />
                <table>
                    <thead>
                        <tr>
                            <th>Team</th>
                            <th>Player</th>
                            <th>Size</th>
                            <th>Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredJerseys.map(jersey => (
                            <JerseyTableRow key={jersey.id} jersey={jersey} />
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default JerseyTable;
