import React, {Component} from 'react';
import JerseyTableRow from './JerseyTableRow';

const JerseySort = ({ jerseys }) => {
  const [sortedJerseys, setSortedJerseys] = useState(jerseys);
  const [sortType, setSortType] = useState('');

  const handleSort = (type) => {
    let sortedArray = [...jerseys];

    switch (type) {
      case 'team':
        sortedArray.sort((a, b) => a.team.localeCompare(b.team));
        break;
      case 'player':
        sortedArray.sort((a, b) => a.player.localeCompare(b.player));
        break;
      case 'number':
        sortedArray.sort((a, b) => a.number - b.number);
        break;
      case 'price':
        sortedArray.sort((a, b) => a.price - b.price);
        break;
      default:
        break;
    }

    setSortedJerseys(sortedArray);
    setSortType(type);
  };

  return (
    <div>
      <button onClick={() => handleSort('team')}>Sort by Team</button>
      <button onClick={() => handleSort('player')}>Sort by Player</button>
      <button onClick={() => handleSort('number')}>Sort by Number</button>
      <button onClick={() => handleSort('price')}>Sort by Price</button>

      <table>
        <thead>
          <tr>
            <th>Team</th>
            <th>Player</th>
            <th>Number</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {sortedJerseys.map((jersey) => (
            <tr key={jersey.id}>
              <td>{jersey.team}</td>
              <td>{jersey.player}</td>
              <td>{jersey.number}</td>
              <td>{jersey.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default JerseySort;
