import React, { Component } from "react"
import JerseyTableRow from "./JerseyTableRow"

export default class JerseyTable extends Component {
    constructor(props) {
        super(props)
        this.state = {
            jerseys: [],
            sortColumn: null,
            sortDirection: null
        }
    }

    handleSort = (column) => {
        let { jerseys, sortColumn, sortDirection } = this.state;

        if (sortColumn === column) {
            sortDirection = sortDirection === "asc" ? "desc" : "asc";
        } else {
            sortColumn = column;
            sortDirection = "asc";
        }

        // Sort the jerseys array based on the selected column and direction
        jerseys.sort((a, b) => {
            if (a[column] < b[column]) return sortDirection === "asc" ? -1 : 1;
            if (a[column] > b[column]) return sortDirection === "asc" ? 1 : -1;
            return 0;
        });

        this.setState({ jerseys, sortColumn, sortDirection });
    }

    render() {
        const { jerseys, sortColumn, sortDirection } = this.state;

        return (
            <table>
                <thead>
                    <tr>
                        <th onClick={() => this.handleSort("Player")}>
                            Player {sortColumn === "Player" && <span>{sortDirection === "asc" ? "▲" : "▼"}</span>}
                        </th>
                        <th onClick={() => this.handleSort("Number")}>
                            Number {sortColumn === "Number" && <span>{sortDirection === "asc" ? "▲" : "▼"}</span>}
                        </th>
                        <th onClick={() => this.handleSort("Price")}>
                            Price {sortColumn === "Price" && <span>{sortDirection === "asc" ? "▲" : "▼"}</span>}
                        </th>
                        <th onClick={() => this.handleSort("Team")}>
                            Team {sortColumn === "Team" && <span>{sortDirection === "asc" ? "▲" : "▼"}</span>}
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {jerseys.map((jersey) => <JerseyTableRow key={jersey._id} jersey={jersey} />)}
                </tbody>
            </table>
        )
    }
}
