import React, { Component } from "react";
import { BrowserRouter, Switch, Route } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.css";
import "./css/App.css";
import Register from "./components/Register";
import ResetDatabase from "./components/ResetDatabase";
import Login from "./components/Login";
import Logout from "./components/Logout";
import AddCar from "./components/AddCar";
import EditCar from "./components/EditCar";
import DeleteCar from "./components/DeleteCar";
import DisplayAllCars from "./components/DisplayAllCars";
import LoggedInRoute from "./components/LoggedInRoute";
import BuyCar from "./components/BuyCar";
import PayPalMessage from "./components/PayPalMessage";
import JerseySearch from "./components/JerseySearch"; // Import the JerseySearch component
import JerseyTable from "./components/JerseyTable"; // Import the JerseyTable component
import JerseyTableRow from "./components/JerseyTableRow"; // Import the JerseyTableRow component

import { ACCESS_LEVEL_GUEST } from "./config/global_constants";

if (typeof localStorage.accessLevel === "undefined") {
    localStorage.name = "GUEST";
    localStorage.accessLevel = ACCESS_LEVEL_GUEST;
    localStorage.token = null;
    localStorage.profilePhoto = null;
}

export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            jerseys: [], // Your initial list of jerseys
            searchedJerseys: [] // Searched jerseys will be stored here
        };
    }

    componentDidMount() {
        // Fetch your initial list of jerseys here
        this.fetchJerseys();
    }

    fetchJerseys = () => {
        // Simulate fetching data from an API or any other source
        // Replace this with your actual fetch logic
        const mockJerseys = [
            { _id: 1, team: "Team A", player: "Player 1", number: 10, price: 100 },
            { _id: 2, team: "Team B", player: "Player 2", number: 20, price: 150 },
            { _id: 3, team: "Team A", player: "Player 3", number: 30, price: 120 },
            { _id: 4, team: "Team C", player: "Player 4", number: 40, price: 130 }
            // Add more jersey objects as needed
        ];
        this.setState({ jerseys: mockJerseys });
    };

    handleSearch = (searchTerm) => {
        // Implement search logic here
        const searchedJerseys = this.state.jerseys.filter(jersey => {
            return jersey.team.toLowerCase().includes(searchTerm.toLowerCase()) || jersey.player.toLowerCase().includes(searchTerm.toLowerCase());
        });
        this.setState({ searchedJerseys });
    };

    render() {
        return (
            <BrowserRouter>
                <div>
                    <JerseySearch onSearch={this.handleSearch} />
                    <Switch>
                        <Route exact path="/Register" component={Register} />
                        <Route exact path="/ResetDatabase" component={ResetDatabase} />
                        <Route exact path="/" component={DisplayAllCars} />
                        <Route exact path="/Login" component={Login} />
                        <Route exact path="/BuyCar/:id" component={BuyCar} />
                        <Route exact path="/PayPalMessage/:messageType/:payPalPaymentID" component={PayPalMessage} />
                        <LoggedInRoute exact path="/Logout" component={Logout} />
                        <LoggedInRoute exact path="/AddCar" component={AddCar} />
                        <LoggedInRoute exact path="/EditCar/:id" component={EditCar} />
                        <LoggedInRoute exact path="/DeleteCar/:id" component={DeleteCar} />
                        <Route exact path="/DisplayAllCars" component={DisplayAllCars} />
                        <Route path="*" component={DisplayAllCars} />
                    </Switch>
                    <JerseyTable jerseys={this.state.searchedJerseys.length > 0 ? this.state.searchedJerseys : this.state.jerseys} />
                </div>
            </BrowserRouter>
        );
    }
}
