import React from 'react';

function FilterJersey({ teamFilter, sizeFilter, onFilterChange }) {
    return (
        <div>
            <select
                value={teamFilter}
                onChange={(e) => onFilterChange('teamFilter', e.target.value)}
            >
                <option value="">All Teams</option>
                {/* Options should be dynamically generated based on available teams */}
                <option value="Team A">Team A</option>
                <option value="Team B">Team B</option>
            </select>
            <select
                value={sizeFilter}
                onChange={(e) => onFilterChange('sizeFilter', e.target.value)}
            >
                <option value="">All Sizes</option>
                {/* Similarly, dynamically generate size options */}
                <option value="S">S</option>
                <option value="M">M</option>
            </select>
            {/* Add more filters here if necessary */}
        </div>
    );
}

export default FilterJersey;
